import React from 'react';

class Form extends React.Component {
  constructor() {
    super();

    this.handleTextAreaChange = this.handleTextAreaChange.bind(this)

    this.state = {
      cidade: '',
      name: '',
      idade: '',
      text: '',
    };
  }

  handleTextAreaChange(event) {
    this.setState({ text: event.target.value });
  }

  render() {
    return (
      <form className="form">
        <label for="cidade">
          Cidade:
          <select name="cidade" className="cidade">
            <option value="value0"> </option>
            <option value="value1">bh</option>
            <option value="value2">rj</option>
            <option value="value3">sp</option>
          </select>
        </label>{' '}
        <br />
        <label for="name">
          Nome:
          <input name="name" type="text"></input>
        </label>
        <br />
        <label for="idade">
          Idade:
          <input name="idade" type="textnumber"></input>
        </label>
        <br />
        <label for="text">
          Texto:
          <textarea name="text" value={this.state.text} onChange={this.handleTextAreaChange} />
        </label>
        <br />
      </form>
    );
  }
}

export default Form;
